export class TabbedFileEditor {
    // ... existing methods

    async handleDrop(event) {
        event.preventDefault();
        const fileHandle = JSON.parse(event.dataTransfer.getData('application/json'));
        const targetElement = event.target.closest('[data-full-path]');
        if (targetElement) {
            const targetPath = targetElement.dataset.fullPath;
            alert(`Dropped ${fileHandle.name} to ${targetPath}`);
            try {
                const parentHandle = await this.resolveParentHandle(fileHandle);
                // Check if the parent handle was successfully retrieved
                if (!parentHandle) {
                    throw new Error('Cannot resolve parent handle for the file drop.');
                }
                const newFileHandle = await parentHandle.getFileHandle(targetPath.split('/').pop(), { create: true });
                const writable = await newFileHandle.createWritable();
                const file = await fileHandle.getFile();
                const content = await file.text();
                await writable.write(content);
                await writable.close();
                // Remove the original file after copying
                await parentHandle.removeEntry(fileHandle.name);
                console.log(`Moved ${fileHandle.name} to ${targetPath}`);
                await this.refreshFileTree();
            } catch (error) {
                console.error('Error moving file:', error);
                alert(`Failed to move file: ${error.message}`);
            }
        }
    }

    async resolveParentHandle(fileHandle) {
        const filePath = this.filePaths.get(fileHandle);
        if (!filePath) {
            console.warn('No file path found for the provided file handle. Attempting to log available file paths.');
            console.log('Current file paths:', Array.from(this.filePaths.entries()));
            return null;
        }
        const pathParts = filePath.split('/');
        pathParts.pop(); // Remove the file name from path
        let currentHandle = this.directoryHandle;

        for (const part of pathParts) {
            if (!part) {
                continue; // Skip empty parts
            }
            try {
                currentHandle = await currentHandle.getDirectoryHandle(part, { create: false });
            } catch {
                console.error(`Could not resolve the directory handle for part: ${part}`);
                return null;
            }
        }
        return currentHandle;
    }
}
